import React, { useState, useRef, useEffect } from "react";
import { Collapse, Navbar, NavbarToggler, Nav, NavItem, Button, UncontrolledButtonDropdown, DropdownToggle, DropdownMenu, DropdownItem } from "reactstrap";
import { useAuth0 } from "@auth0/auth0-react";
import { NavLink as RouterNavLink, Link } from "react-router-dom";
import styled from "styled-components";

//Styling
const NavStyle = styled.div`
  .logo {
    border-radius: 40px;
    height: 80px;
    justify-content: right;
    margin top: 0px;
    width: 80px;
  }
  a {
    color: #ffffff;
  }
  .navbar{
    background-color:#451814;
    height: 80px;
    left: 0;
    position: fixed;
    right: 0;
    top: 0;
  }
`

const NavBar = () => {
  //Variables and States
  const [navBackground, setNavBackground] = useState()
  // const navRef = useRef()
  // navRef.current = navBackground

  // useEffect(() => {
  //   const handleScroll = () => {
  //     const show = window.scrollY > 450
  //     if (navRef.current !== show) {
  //       setNavBackground(show)
  //     }
  //   }
  //   document.addEventListener('scroll', handleScroll)
  //   return () => {
  //     document.removeEventListener('scroll', handleScroll)
  //   }
  // }, [])

  const [isOpen, setIsOpen] = useState(false);

  const {
    user,
    isAuthenticated,
    loginWithRedirect,
    logout,
  } = useAuth0();

  const toggle = () => setIsOpen(!isOpen);

  const logoutWithRedirect = () =>
    logout({
      returnTo: window.location.origin,
    });

  return (
    <NavStyle>
      <div>
        <Navbar className="navbar">
        <img src="./logo.png" className="logo" alt="logo" />
          <Link to ="/Home">HOME</Link>
          {/* <NavbarToggler onClick={toggle} className="hamburger" /> */}
          {/* <Collapse isOpen={isOpen} navbar> */}
            <Nav className="mr-auto" navbar>
              <NavItem>
                <Link to ="/notes">Notes</Link>
              </NavItem>
              {/* <NavItem>
                <Link to ="/farmers-markets">Farmers Market</Link>
              </NavItem> */}
            </Nav>
            <Nav className="d-none d-md-block" navbar>
              {!isAuthenticated && (
                <NavItem>
                  <Button
                    id="qsLoginBtn"
                    color="primary"
                    className="btn-margin"
                    onClick={() => loginWithRedirect()}
                  >
                    Log in
                  </Button>
                </NavItem>
              )}
              {/* Add User picture, Grocery List link, and Log out link if they are logged in */}
              {isAuthenticated && (
                <UncontrolledButtonDropdown>
                <DropdownToggle caret>
                  Dropdown
                </DropdownToggle>
                <DropdownMenu>
                  <DropdownItem header>Header</DropdownItem>
                  <DropdownItem disabled>Action</DropdownItem>
                  <DropdownItem>Another Action</DropdownItem>
                  <DropdownItem divider />
                  <DropdownItem>Another Action</DropdownItem>
                </DropdownMenu>
              </UncontrolledButtonDropdown>
              )}
            </Nav>
          {/* </Collapse> */}
        </Navbar>
      </div>
    </NavStyle>
  );
}

export default NavBar;
