import React, { useState } from "react";
import { useAuth0 } from "@auth0/auth0-react";
import styled from "styled-components";
import Calendar from "react-calendar";
import 'react-calendar/dist/Calendar.css';
import { Button, Dropdown, DropdownItem, DropdownMenu, DropdownToggle, Form, FormGroup, Label, Input, FormText } from "reactstrap"
import AddProduction from "../components/addProduction";
import API from "../utils/API";


const StyleWrapper = styled.div`
    margin: 0 50px;
    h1 {
        color:  #451814;
        justify-content: center;
        margin-top: 150px;
        text-align: center;
    }
    body {
        justify-content: center;
        text-align: center;
    }
    .calendar {
        border: 10px solid #dfc140;
        border-radius: 10px;
        margin: auto;
        margin-bottom: 50px;
    }
    h2 {
        color: #a95534
        margin-top: 30px;
    }
    .dropdown {
        margin: 20px;
    }
    .upload {
        align-items: center;
        justify-content: center;
        margin: auto;
        text-align: center;
    }
`


const Notes = () => {

    const { isAuthenticated } = useAuth0;

    const [dropdownOpen, setDropdownOpen] = useState(false);

    const toggle = () => setDropdownOpen(prevState => !prevState)

    const [note, setNote] = useState({
        title: "",
        text: "",
        noteDate: "",
        production: ""
    });

    const { title, text, noteDate, production } = note

    const [date, setDate] = useState("");

    const handleSetDate = () => {
        setDate({})
    }

    const createNote = () => {
        API.addNote()
    }

    const handleInputChange = event => {
        setNote({
            title: title,
            text: text,
            noteDate: date,
            production: production,
         });
      }

    const handleFormSubmit = (event) => {
        event.preventDefault();
        createNote();

    }
   return (
       <StyleWrapper>
           <header className="App-header">

              <h1 className="header">
              Rehearsal Notes
              </h1>
          </header>
          <body>
              <p>Select your rehearsal date</p>
              <Calendar 
              className="calendar"
              onClick={handleSetDate}
              />
              <Form>
                  {isAuthenticated ? (

                    <Dropdown 
                    group isOpen={dropdownOpen} 
                    size="md" 
                    toggle={toggle}
                    className="dropdown"
                    >
                        <DropdownToggle caret>
                            Select a production from your list
                        </DropdownToggle>
                        <DropdownMenu>
                            <DropdownItem>Another Action</DropdownItem>
                            <DropdownItem>Another Action</DropdownItem>
                            <DropdownItem>Another Action</DropdownItem>
                            <DropdownItem>Another Action</DropdownItem>
                        </DropdownMenu>
                    </Dropdown>

                  ) : <div></div>}
              {/* <h2>Enter your rehearsal notes here</h2> */}
                  <FormGroup>
                      <Input type="text" value={title} placeholder="Note Title"/>
                  </FormGroup>
                  <FormGroup>
                      <Input type="text" value={production} placeholder="Production Name"/>
                  </FormGroup>
                  <FormGroup>
                      <Input type="textarea" value={text} id="exampleText" placeholder="Note Text"/>
                  </FormGroup>
                      <Button
                      onClick={handleFormSubmit}
                      >Submit
                      </Button>
                  <FormGroup className="upload">
                      {/* <Label for="exampleFile">File</Label> */}
                      <Input type="file" name="file" id="exampleFile" />
                      <FormText color="muted">
                      Upload your script copy to save an electronic version
                      </FormText>
                  </FormGroup>
              </Form>
          </body>
       </StyleWrapper>
   )

}

export default Notes;
